<?phpfunction root($path = null){	return base_url('root/'.$path);}function res($path){	return root('res/'.$path);}function e($text = null){	echo $text;}function b($text){	return '<b>'.$text.'</b>';}function h1($text){	return '<h1>'.$text.'</h1>';}function h2($text){	return '<h2>'.$text.'</h2>';}function h3($text){	return '<h3>'.$text.'</h3>';}function div($text,$id=null,$class=null){	if(isset($id)){$id = 'id="'.$id.'"';}	if(isset($class)){$class = 'class="'.$class.'"';}	return '<div '.$id.' '.$class.'>'.$text.'</div>';}function pr($val = array()){	if(is_array($val)){		return print_r($val);	}}function br(){	return '<br/>';}function a($text,$link,$attr=array()){	return '<a href="'.$link.'" '.attr($attr).'>'.$text.'</a>';}function img($src,$attr=array(),$outsrc = false){	if(!$outsrc){$link = './resource/img/'.$src;}	else{$link = $src;}	if(file_exists($link)){		return '<img src="'.$link.'" '.attr($attr).' />';	}	else{return false;}}function li($text){	return '<li>'.$text.'</li>';}function attr($attr = array()){	if(is_array($attr)){		foreach($attr as $name => $var){			$join .= $name.'="'.$var.'" ';		}		return $join;	}}?>