<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Config_model extends CI_Model{private $table;	/**	 * define config table	 **/	public function __CONSTRUCT(){		parent::__CONSTRUCT();		$this->table = array(			'config' => $this->db->dbprefix('config'),		);	}	/**	 * get config module	 * @param $id_module string	 * @return array	 **/	public function get($id_module){		$query = $this->db->get_where($this->table['config'],array('id_module' => $id_module),50);		$data['result'] = false;		$data['id_module'] = $id_module;		if($query->num_rows() > 0){			foreach($query->result() as $row){				$data['setting'][$row->name] = $row->val;			}			$data['result'] = true;		}		else{			$this->error->set($this->error_lang['config_not_found']);		}		return $data;	}	/**	 * add new config module	 * @param $data array	 * $return mix	 **/	public function add($data = array()){		if(is_array($data)){			return $this->db->insert($this->table['config'],$data);		}	}	/**	 * update config module	 * @param $id_module string	 * @param $name string	 * @param $var string	 * @return mix	 **/	public function set_value($id_module,$name,$val){		$this->db->where(array('id_module' => $id_module, 'name' => $name));		return $this->db->update($this->table['config'],array('val' => $val));		}	/**	 * check avaiable config	 * @param $id_module string	 * @param $name string	 * @return booelan	 **/	public function is_exsist($id_module,$name){		$query = $this->db->get_where($this->table['config'],array('id_module'=>$id_module,'name'=>$name),1);		if($query->num_rows()==1){return true;}	}}?>